// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract crowdSale1 {

    IERC20 token;
    event Transfer1(address from, address to, uint256 amount);

  
/// @custom:oz-upgrades-unsafe-allow constructor
    constructor(){
    //  function initialize() public initializer {
       token = IERC20(0x8A3F677B7f738811d9951A848002731085f469fe);
     }
      receive() payable external {}

    function tokenForEth() payable external {
      uint256 amount = msg.value;
      uint256 calValue = amount * 10;
      require(token.balanceOf(address(this)) >= calValue);
      token.transfer(msg.sender, calValue);
      emit Transfer1(address(this), msg.sender, calValue);
    }
}
